import styled from 'styled-components';

export const Container = styled.div`
  width: 100%;
  max-width: 100%;
  height: 100%;
  max-height: 100%;
  display: flex;
  flex-direction: row;

`;

export const Form = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  flex: 1;

  h1{
   font-weight: 400;
   font-size: 24px;
   margin-top: 90px;
  }

  input{
    height: 40px;
    width: 240px;
    margin-top: 40px;
    border: none;
    background-color: #f8f8f8;
    border-radius: 3px;
    padding-left: 15px;
    font-size: 12px;
  }

  button{    
    height: 40px;
    width: 240px;
    margin-top: 12px;
    border-radius: 3px;
    border: none;
    background-color: #018BD0;  
    color: #ffffff;  
    font-weight: 600;
    font-size: 12px;
    transition: all 150ms ease-in-out;
    cursor: pointer;

    &:active{
      opacity: 0.6;
    }
  }
`;

export const Logo = styled.img`
  margin-top: 140px;
  height: 70px;
  width: 70px;
`;

export const Image = styled.img`
  width: 1100px;
  height: 100%;
  object-fit: cover;
  object-position: center center; 
`;
