import styled from 'styled-components';

export const Container = styled.div`
  width: 100%;
  max-width: 100%;
  height: 100%;
  max-height: 100%;
  display: flex;
  flex-direction: row;

`;

export const Form = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 835px;  
  flex:1;

  img{
    margin-top: 215px;
    height: 75px;
    width: 75px;
  }

  h1{
   font-weight: 400;
   font-size: 28px;
   margin-top: 110px;
  }

  input{
    width: 300px;
    margin-top: 50px;    
  }

  button{    
    width: 300px;
    margin-top: 20px;    
  }
`;

export const Image = styled.img`
  height: 100%;
  width: calc(100vw - 835px);
  flex:1;
  object-fit: cover;
  object-position: center center; 
`;

export const Help = styled.div`
  position: absolute;
  display: inline-flex;
  height: 30px;
  width: 30px;
  justify-content: center;
  align-items: center;
  bottom : 0;
  margin-left: 25px;
  margin-bottom: 25px;
  align-self: flex-start;
`;
